/* Header */
.slideshow-wrapper {
    .slideshow-inner {
        &.has-leftCategories {
            &.has-rightBanners {
                @media screen and (min-width: 551px) {
                    width: calc(80% - 10px);
                }

                @media screen and (min-width: 1440px) {
                    width: calc(47.3% + 15.1%);
                }
            }
        }
    }

    .slideshow-right-banner {
        @media screen and (min-width: 551px) {
            width: 20%;

            .banner-list {
                .banner-item {
                    width: 100%;

                    &:nth-child(2) {
                        margin-top: 10px;
                    }
                }
            }
        }

        @media screen and (min-width: 1440px) {
            width: calc(17.3% - 10px);

            .banner-list {
                .banner-item {
                    width: 100%;
                }
            }
        }
    }
}

.header-layout-2 {
    .header-top {
        .header-top-left {
            @media screen and (min-width: 1025px) {
                width: fit-content;
            }

            .navUser-item {
                &.navUser-item--phone {
                    display: none;
                }
            }
        }

        .header-top-middle {
            flex-grow: 1;

            .dropdown--quickSearch {
                max-width: 100%;

                .form-field {
                    .form-input {
                        max-width: 100%;
                    }
                }
            }
        }

        .header-top-right {
            @media screen and (min-width: 1025px) {
                width: fit-content;
            }
            .navUser-section {
                .navUser-item {
                    &.navUser-item--giftCard {
                        display: none;
                    }

                    &.navUser-item--cart > .navUser-action {
                        padding-bottom: 0;

                        .countPill {
                            right: -15px;
                            top: -15px;
                        }

                        & > .icon {
                            width: 25px;
                            height: 25px;
                        }

                        .navUser-item-cartLabel {
                            display: block;
                        }
                    }
                }
            }
        }
    }
}

/* Search on Header Sticky */
#quickSearch {
    &.dropdown--quickSearchSticky {
        @media screen and (min-width: 1200px) {
            display: block;
            top: 1px;
            left: 45%;
            max-width: 550px;

            .form-field {
                box-shadow: rgba(0, 0, 0, 0.16) 0px 1px 4px;
            }
        }

        @media screen and (min-width: 1400px) {
            left: 35%;
            max-width: 638px;
        }
    }
}

/* Card */
.card {
    .card-figure {
        overflow: visible;
        .card-figcaption {
            opacity: 1;
            transform: none;
            top: 8px;
            right: 0;
            left: unset;

            .card-figcaption-body {
                transform: none;
                .wishlist {
                    padding: 6px;
                    border-radius: 50%;
                    -webkit-border-radius: 50%;
                    -moz-border-radius: 50%;
                    -ms-border-radius: 50%;
                    -o-border-radius: 50%;
                    background: stencilColor('card-icon-bgColor');
                    @include breakpoint('small') {
                        width: 36px;
                        height: 36px;
                        margin-top: 7px;
                    }
                    @include breakpoint('medium') {
                        width: 40px;
                        height: 40px;
                    }
                    &.show-mobile {
                        @media (max-width: 1024px) {
                            display: -ms-flexbox;
                            display: -webkit-flex;
                            display: flex;
                            -ms-flex-align: center;
                            -webkit-align-items: center;
                            align-items: center;
                            -ms-flex-pack: center;
                            -webkit-justify-content: center;
                            justify-content: center;
                            -ms-flex-wrap: wrap;
                            -webkit-flex-wrap: wrap;
                            flex-wrap: wrap;
                        }
                    }
                    svg {
                        width: 18px;
                        height: 15px;
                        fill: stencilColor('card-icon-color');
                        @media (min-width: 361px) {
                            width: 20px;
                            height: 17px;
                        }
                        @include breakpoint('small') {
                            width: 22px;
                            height: 20px;
                        }
                    }
                    &:hover {
                        @include breakpoint('small') {
                            background: stencilColor('card-icon-bgColor-hover');
                        }
                        svg {
                            fill: stencilColor('card-icon-bgColor-hover');
                            @include breakpoint('small') {
                                fill: stencilColor('card-icon-color-hover');
                            }
                        }
                    }
                    &.show-mobile {
                        display: -ms-flexbox;
                        display: -webkit-flex;
                        display: flex;
                        -ms-flex-align: center;
                        -webkit-align-items: center;
                        align-items: center;
                        -ms-flex-pack: center;
                        -webkit-justify-content: center;
                        justify-content: center;
                        -ms-flex-wrap: wrap;
                        -webkit-flex-wrap: wrap;
                        flex-wrap: wrap;
                    }
                }
            }
        }
    }
    .card-body {
        .custom-brand-sku-wrapper {
            display: flex;
            align-items: center;
            flex-wrap: wrap;
            min-height: 44px;
            text-transform: uppercase;

            @media screen and (max-width: 767.98px) {
                min-height: 66px;
            }

            .card-brand {
                position: relative;
                margin-bottom: 0;
                padding-right: 8px;
                margin-right: 8px;
                border-right: 1px solid darkgray;
                font-size: 12px;
            }

            .card-sku {
                font-size: 12px;
                color: stencilColor('color-textSecondary');
                a {
                    color: stencilColor('color-textSecondary');
                }
                margin-bottom: 0;
            }
        }

        .card-title {
            margin-bottom: 0;
            > a.clamp {
                min-height: 86px;
                -webkit-line-clamp: 4 !important;
            }
        }

        .card-figcaption-bottom {
            .card-figcaption-body {
                max-width: 100%;

                .card-figcaption-button {
                    width: 100%;
                    max-width: 100%;
                }
            }
        }
    }
}

