.heroCarousel {
    .slick {
        &-arrow {
            background-color: transparent;
            border: none;
            span {
                display: none;
            }
        }
        &-next {
            &::before {
                width: 24px;
                height: 24px;
            }
        }
        &-prev {
            &::before {
                width: 24px;
                height: 24px;
            }
        }
        &-dots {
            .slick-active {
                button {
                    background-color: gray;
                    span {
                        display: none;
                    }
                }
            }
            button {
                span {
                    display: none;
                }
            }
        }
    }
}
// Change dots slick slider
.slick-dots li.slick-active button,
.slick-dots li:hover button {
    background: #424549;
