    margin: 0
}

.prevent-scroll {
    overflow: hidden
}

.break-word {
    word-wrap: break-word
}

.mb-20 {
    margin-bottom: 20px
}

@media (max-width:779.98px) {
    .hide-sm {
        display: none
    }
}

.break-text {
    word-wrap: break-word
}

.order-amount-container {
    min-width: 120px
}

.order-amount-container .order-amount-decrease-btn,
.order-amount-container .order-amount-increase-btn {
    padding: 1px;
    box-sizing: border-box;
    height: 38px;
    border: 1px solid #cdcdcd;
    background-color: #fff
}

.order-amount-container .order-amount-decrease-btn {
    border-radius: 2px 0 0 2px;
    border-right: none
}

.order-amount-container .order-amount-increase-btn {
    border-radius: 0 2px 2px 0;
    border-left: none
}

.order-amount-container .qtyField::-webkit-inner-spin-button,
.order-amount-container .qtyField::-webkit-outer-spin-button {
    -webkit-appearance: none;
    margin: 0
}

.order-amount-container .qtyField {
    padding: 0;
    color: #333;
    font-size: 14px;
    -moz-appearance: textfield;
    text-align: center;
    min-width: 1px;
    flex-shrink: 1
}

.quantity-selector {
    align-items: stretch;
    display: flex
}

.quantity-selector__input {
    flex: 1;
    font-size: 14px;
    min-width: 3.125rem;
    text-align: center
}

.quantity-selector__input::-webkit-inner-spin-button,
.quantity-selector__input::-webkit-outer-spin-button {
    -webkit-appearance: none;
    appearance: none;
    margin: 0
}

.quantity-selector__input.input-control {
    color: inherit;
    height: auto;
    padding: 0;
    z-index: 1;
    -moz-appearance: textfield
}

.quantity-selector__input[readonly] {
    cursor: text !important;
    background-color: #fff !important
}

.quantity-selector__control {
    align-items: center;
    background: #fff;
    border: 1px solid #cdcdcd;
    color: #ff7000;
    display: flex;
    justify-content: center;
    padding: 0
}

.quantity-selector__control--decrease {
    border-radius: 2px 0 0 2px;
    border-right: none;
    padding-right: 1px
}

.quantity-selector__control--increase {
    border-left: none;
    border-radius: 0 2px 2px 0;
    padding-left: 1px
}

.quantity-selector__control:disabled {
    color: #cdcdcd
}

.quantityDropdown-wrapper {
    width: auto;
    background-color: #fff;
    padding: 20px 20px 11px 20px;
    border: 1px solid #cdcdcd;
    border-radius: 2px;
    z-index: 999;
    position: absolute;
    left: 0;
    right: 0;
    box-shadow: 0 2px 4px 0 rgba(0, 0, 0, .25);
    display: flex;
    align-items: center;
    flex-direction: column;
    padding: 10px 15px;
    width: 110px;
    left: 38px
}

.quantityDropdown-wrapper .dropdown-search-box {
    display: flex;
    flex: 0 0 auto;
    margin-bottom: 10px;
    height: 38px;
    width: 100%;
    padding: 6px 12px;
    line-height: 1.5;
    color: #5c5c5c;
    background-image: none;
    border: 1px solid #cdcdcd;
    border-radius: 2px;
    box-shadow: none;
    -webkit-appearance: none
}

.quantityDropdown-wrapper .dropdown-search-box:focus {
    border: 1px solid #333;
    box-shadow: none
}

.quantityDropdown-wrapper .dropdown-search-icon {
    position: absolute;
    right: 27px;
    top: 26px;
    color: #cdcdcd
}

.quantityDropdown-wrapper .form-controls {
    padding-left: 0
}

.quantityDropdown-wrapper .noUi-target {
    margin-left: 0
}

.quantityDropdown-wrapper .button-wrapper {
    display: flex;
    flex: 0 0 auto;
    padding-left: 0
}

.quantityDropdown-wrapper ul {
    list-style-position: outside;
    list-style-type: none;
    background-color: #fff;
    max-height: 300px;
    width: 100%;
    overflow-y: auto;
    overflow-x: hidden;
    margin: 0;
    padding: 0;
    padding-right: 10px
}

.quantityDropdown-wrapper ul::-webkit-scrollbar {
    width: 6px
}

.quantityDropdown-wrapper ul::-webkit-scrollbar-track {
    background-color: #eaeaea;
    border-left: 1px solid #ccc
}

.quantityDropdown-wrapper ul::-webkit-scrollbar-thumb {
    background-color: #ccc
}

.quantityDropdown-wrapper ul::-webkit-scrollbar-thumb:hover {
    background-color: #aaa
}

.quantityDropdown-wrapper ul li {
    margin-top: 0;
    padding: 0;
    width: 100%;
    position: relative
}

.quantityDropdown-wrapper ul li:hover {
    background-color: #f5f5f5
}

.quantityDropdown-wrapper ul li label {
    display: flex;
    align-items: center;
    line-height: 20px;
    padding: 6px 0;
    width: 100%
}

@media (max-width:959.98px) {
    .quantityDropdown-wrapper ul li label {
        padding: 10px 0 10px 5px
    }
}

.quantityDropdown-wrapper ul li label.subcategory-label {
    padding: 10px 0
}

.quantityDropdown-wrapper ul li input[type=checkbox] {
    position: absolute;
    left: -50px
}

.quantityDropdown-wrapper ul li input[type=checkbox]~.svgIcon-checkbox {
    padding-right: 5px
}

.quantityDropdown-wrapper ul.categoryFacet-list li {
    background-color: initial
}

.quantityDropdown-wrapper ul.categoryFacet-list li.facet-category--checked {
    background-color: #e7e7e7
}

.quantityDropdown-wrapper .quantityDropdown-container {
    padding: 0
}

.quantityDropdown-wrapper .quantityDropdown-container .quantityDropdown-entry {
    margin: 0;
    text-align: center;
    line-height: 40px
}

.quantityDropdown-wrapper .quantityDropdown-container .quantityDropdown-entry:before {
    content: "";
    width: 5px;
    height: 100%;
    background-color: transparent;
    position: absolute;
    left: 0
}

.quantityDropdown-wrapper .quantityDropdown-container .quantityDropdown-entry:hover {
    color: inherit
}

.quantityDropdown-wrapper .quantityDropdown-container .quantityDropdown-entry:hover:before {
    background-color: #ff7000
}

.orderBox .quantityDropdown-wrapper {
    left: 50px
}

.autosuggest {
    position: relative
}

.autosuggest .input-control {
    background: #fff;
    border-radius: 2px;
    color: #333
}

.autosuggest .input-control.is-invalid {
    background: #f4cfd5;
    border-color: #c7102d
}

.autosuggest__flyout {
    background: #fff;
    border: 1px solid #cdcdcd;
    box-shadow: 0 3px 4px rgba(0, 0, 0, .15);
    left: 0;
    max-height: 60vh;
    overflow-y: auto;
    position: absolute;
    top: calc(100% + 4px);
    width: 100%;
    z-index: 1
}

.autosuggest__input-wrapper {
    position: relative
}

.autosuggest__clear-btn,
.autosuggest__remove-btn {
    background: 0 0;
    border: 0;
    color: inherit;
    display: flex;
    font: inherit;
    margin: 0;
    padding: .5rem
}

.autosuggest__clear-btn {
    display: none;
    position: absolute;
    right: 0;
    top: 50%;
    transform: translateY(-50%)
}

.autosuggest__remove-btn::before {
    content: "";
    display: block;
    height: 100%;
    left: 0;
    position: absolute;
    top: 0;
    width: 100%
}

.autosuggest__remove-btn:focus,
.autosuggest__remove-btn:focus-within {
    color: #ff7000
}

.autosuggest__input.input-control {
    padding-right: 2rem
}

.autosuggest__input--not-empty:not([hidden])+.autosuggest__clear-btn {
    display: block
}

.autosuggest__static-container.input-control {
    padding-right: 0
}

.autosuggest__static-text {
    color: #333;
    max-width: 100%;
    overflow: hidden;
    text-overflow: ellipsis;
    white-space: nowrap
}

.product-option {
    align-items: center;
    display: flex;
    font-size: 14px;
    line-height: 1.42857;
    width: 100%
}

.product-option__image-column {
    flex: 0 0 auto;
