.hds-ToastLayout {
    position: relative
}

.hds-ToastLayout__content {
    position: relative;
    z-index: 10
}

.hds-ToastLayout__toasts {
    position: absolute;
    top: 2rem;
    right: 2rem;
    left: 2rem;
    z-index: 20;
    pointer-events: none
}

.hds-ToastLayout__toast {
    max-width: 100%;
    margin-bottom: .75rem;
    pointer-events: auto;
    transition: transform .3s ease-out, opacity .3s ease-out, height .3s ease-out, margin .3s ease-out
}

.hds-ToastLayout__toast-enter {
    opacity: 0;
    transform: translateY(2rem)
}

.hds-ToastLayout__toast-enter-active {
    opacity: 1;
    transform: none
}

.hds-ToastLayout__toast-exit {
    opacity: 1
}

.hds-ToastLayout__toast-exit-active {
    margin: 0;
    opacity: 0;
    height: 0
}

.hds-Toast {
    position: relative;
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    -webkit-box-align: start;
    -ms-flex-align: start;
    align-items: flex-start;
    -webkit-box-sizing: border-box;
    box-sizing: border-box;
    width: 388px;
    max-width: 100%;
    background: #fff;
    -webkit-box-shadow: 0 0 4px rgba(48, 64, 77, .1), 0 16px 24px rgba(48, 64, 77, .25);
    box-shadow: 0 0 4px rgba(48, 64, 77, .1), 0 16px 24px rgba(48, 64, 77, .25);
    border-radius: 3px;
    border-bottom: 4px solid #ff7000;
    color: #191919;
