.carousel.slider .control {
    position: absolute;
    top: 50%;
    height: 2em;
    width: 1em;
    margin-top: -1em;
    display: inline-block;
    z-index: 2;
    outline: 0 none;
    background: #fff;
    opacity: 0;
    color: #5c5c5c
}

.carousel.slider .control.control-left {
    left: 0;
    border-top: 1px solid #cdcdcd;
    border-bottom: 1px solid #cdcdcd;
    border-right: 1px solid #cdcdcd;
    margin-left: 0
}

