    border-top: 1px solid #cdcdcd;
    border-bottom: 1px solid #cdcdcd;
    border-left: 1px solid #cdcdcd;
    right: 0;
    margin-right: 0
}

.carousel.slider .control.disabled {
    display: block
}

.carousel.slider .control:hover {
    opacity: 1
}

.carousel.slider .control * {
    position: absolute;
    top: 50%;
    margin-top: -.5em
}

.carousel.slider .control.override {
    align-items: center;
    background-color: #fff;
    border: 1px solid;
    border-radius: 2px;
    color: #cdcdcd !important;
    cursor: pointer;
    display: flex;
    height: 40px;
    justify-content: center;
    top: 50%;
    transform: translateY(-50%);
    margin-top: 0;
    opacity: 1 !important;
    width: 40px
}

.carousel.slider .control.override>* {
    margin: 0;
    position: static;
    top: auto
}

.carousel.slider .control.override:hover {
    color: #333 !important
}

.carousel.slider .control.override.disabled {
    display: none
}

.carousel.slider .control.override.control-left {
    left: -10px
}

.carousel.slider .control.override.control-right {
    right: -10px
}

