}

@media (min-width: 551px) {
    .checkoutHeader {
        min-height: 100px;
    }

    .checkoutHeader-content {
        padding: 26px 30px 27px;
    }
}

@media(min-width: 968px) {
    .layout {
        max-width: 1230px;
        padding: 60px 15px;

        .layout-main {
            width: calc(100% - 487px);
            padding-left: 0;
            padding-right: 35px;

            .checkout-steps {
                padding-right: 0;
            }
        }

        .layout-cart {
            width: 487px;
            padding-left: 35px;
            padding-right: 0;

            .optimizedCheckout-orderSummary {
                margin-top: 1.5rem;
            }
        }
