    .checkout-view-content .checkout-form {
        margin-left: 74px;
        margin-right: 74px;
    }
}

@media(max-width: 967px) {
    .cart-section .productList-item .product-actions {
        -webkit-order: 3;
        order: 3;
    }

    .cart-modal-body.optimizedCheckout-orderSummary .cart-section {
        padding-left: 0;
        padding-right: 0;
    }
}

@media(max-width: 550px) {
    .checkout-view-content .customerEmail-container .form-actions  {
        padding-left: 0;
    }
}

.checkout-steps {
    .checkout-step  {
        .checkout-view-header {
