/* Sticky Notification Bar */
.sticky-notification {
    --border-radius: 30px;
    --translate-x: -50%;
    --translate-y: 50px;
    position: fixed;
    bottom: 15px;
    left: 50%;
    transform: translateX(var(--translate-x)) translateY(var(--translate-y));
    pointer-events: none;
    opacity: 0;
    width: max-content;
    max-width: 90%;
    z-index: 999;
    transition: all 450ms ease-in-out, opacity 600ms ease-in-out;
    background: stencilColor("halo_sticky_notification_bar_bg_color");
    padding: 16px 50px;
    border-radius: 30px;
    display: flex;
    flex-direction: row;
    justify-content: center;
    align-items: center;
    gap: 5px;
    overflow: hidden;
    cursor: pointer;
    animation: vertical-floating 1.5s ease-out infinite;
    animation-delay: 3s;
    transform-origin: 50% 50%;
    overflow: initial;
    will-change: transform;
    &.show {
         --translate-y: 0;
        opacity: 1;
        pointer-events: auto;
    }
    .text-wrapper {
        display: flex;
        align-items: center;
        justify-content: center;
        .intro-text {
            color: stencilColor("halo_sticky_notification_bar_color");
            font-size: 14px;
            font-weight: 400;
            transition: color 350ms ease-in-out;
            &:hover {
                color: stencilColor("halo_sticky_notification_bar_color");
            }
        }
        .link-text {
            color: stencilColor("halo_sticky_notification_bar_color");
            font-size: 14px;
            font-weight: 400;
            transition: color 350ms ease-in-out, fill 350ms ease-in-out;
            &:hover {
                color: stencilColor("halo_sticky_notification_bar_color");
            }
        }
        .intro-text-desktop {
            display: block;
        }
        .link-text-desktop {
            display: flex;
            align-items: center;
            justify-content: center;
            gap: 6px;
            margin-left: 6px;
            &:hover {
                .icon {
                    transform: translateX(4px);
                }
            }
        }
        .intro-text-mobile {
            display: none;
        }
        .link-text-mobile {
            display: none;
        }
    }
    .close-btn {
        position: absolute;
        top: 50%;
        right: 20px;
        transform: translateY(-50%);
        cursor: pointer;
        color: #fff;
        font-weight: 300;
        margin-top: -2px;
        svg {
            width: 16px;
            height: 16px;
            fill: stencilColor("halo_sticky_notification_bar_color");
        }
        path {
            fill: stencilColor("halo_sticky_notification_bar_color");
        }
    }
    .icon-link-arrow {
        width: 20px;
        height: 20px;
        fill: stencilColor("halo_sticky_notification_bar_color");
        svg {
            fill: stencilColor("halo_sticky_notification_bar_color");
        }
        rect {
            fill: stencilColor("halo_sticky_notification_bar_color");
        }
        path {
            fill: stencilColor("halo_sticky_notification_bar_color");
        }
    }
    .link-text {
        &:hover {
            .icon-link-arrow {
                fill: stencilColor("halo_sticky_notification_bar_color");
                rect {
                    fill: stencilColor("halo_sticky_notification_bar_color");
                }
                path {
                    fill: stencilColor("halo_sticky_notification_bar_color");
                }
            }
        }
    }
    .mobile-image-icon {
        display: none;
    }
}

@media screen and (max-width: 1024px) {
    .sticky-notification {
        --bottom-arrow-size: 7px;
        --bottom-positon: 66px;
        bottom: 15px;
    }
    #notification-popup {
        width: 80vw;
        max-width: unset;
    }
}

@media screen and (max-width: 767px) {
    .sticky-notification {
        padding: 10px 20px;
        &.hidden-mobile {
            display: none;
        }
        .close-btn {
            display: none;
        }
        .mobile-image-icon {
            display: block;
            width: 18px;
            height: 18px;
            position: relative;
            top: -5px;
            &:first-of-type {
                margin-right: 5px;
            }
            &:nth-of-type(2) {
                margin-left: 5px;
            }
        }
        .text-wrapper {
            flex-direction: column;
            height: 22px;
            justify-content: flex-start;
            overflow: hidden;
            .intro-text-mobile {
                transition: transform 500ms ease-out;
            }
            a {
                transition: transform 500ms ease-out;
            }
            .intro-text-desktop {
                display: none;
            }
            .link-text-desktop {
                display: none;
            }
            .intro-text-mobile {
                display: block;
            }
            .link-text-mobile {
                display: block;
            }
        }
        .text-wrapper.slide-down {
            .intro-text-mobile {
                transform: translateY(-100%);
            }
            a {
                transform: translateY(-100%);
            }
        }
    }
    .sticky-notification.hide-on-mobile {
        display: none;
    }
    .sticky-notification.temporary-hide {
        opacity: 0;
        --translate-y: -20px;
        pointer-events: none;
    }
}

@media screen and (min-width: 768px) {
    .sticky-notification.hidden-desktop {
        display: none;
    }
}

@media screen and (min-width: 1025px) {
    .sticky-notification {
        &::after {
            content: "";
            z-index: -1;
            background-color: hsla(0,0%,100%,.2);
            position: absolute;
            top: -50%;
            bottom: -50%;
            left: -10%;
            width: 3em;
            -webkit-transform: rotate(35deg);
            transform: rotate(35deg);
            visibility: hidden;
        }
    }
}

@keyframes vertical-floating {
    0%, 100% {
        transform: translateX(var(--translate-x)) translateY(0);
    }
    
    50% {
        transform: translateX(var(--translate-x)) translateY(6px);
    }   
}       

@keyframes shineToRight {
    100% {
        left: 110%;
        visibility: visible;
    }
}
